		foreach ( $networks as $CIDR )
		{
			list ( $net, $mask ) = explode ( "/", $CIDR );
			$ip_net = ip2long ( $net );

			$ip_mask = ~((1 << (32 - $mask)) - 1);
			$ip_ip = ip2long ( $IP );
			$ip_ip_net = $ip_ip & $ip_mask;

			if ( $ip_ip_net == $ip_net )
			{
				return true;
			}//if
		}//if

		return false;
	}//function

